import React, { useState } from 'react';

function Quiz() {
    const [score, setScore] = useState(0);
    const [questionIndex, setQuestionIndex] = useState(0);

    const questions = [
        { question: 'What is 2 + 2?', options: ['3', '4', '5'], answer: '4' },
        { question: 'What is 3 + 5?', options: ['8', '7', '6'], answer: '8' },
        { question: 'What is the capital of France?', options: ['Berlin', 'Paris', 'Madrid'], answer: 'Paris' },
        { question: 'Which planet is closest to the Sun?', options: ['Earth', 'Mars', 'Mercury'], answer: 'Mercury' },
        { question: 'What is the square root of 16?', options: ['2', '4', '8'], answer: '4' },
        { question: 'What is the largest ocean on Earth?', options: ['Atlantic', 'Pacific', 'Indian'], answer: 'Pacific' },
        { question: 'Which country is known as the Land of the Rising Sun?', options: ['China', 'Japan', 'Korea'], answer: 'Japan' },
        { question: 'Who wrote the play Romeo and Juliet?', options: ['Shakespeare', 'Dickens', 'Hemingway'], answer: 'Shakespeare' },
        { question: 'What is the chemical symbol for water?', options: ['H2O', 'O2', 'CO2'], answer: 'H2O' },
        { question: 'What is the currency of the United Kingdom?', options: ['Dollar', 'Pound', 'Euro'], answer: 'Pound' },
    ];

    const handleAnswer = (answer) => {
        if (answer === questions[questionIndex].answer) {
            setScore(score + 1); // Increment score if correct
        }
        if (questionIndex + 1 < questions.length) {
            setQuestionIndex(questionIndex + 1); // Move to the next question
        } else {
            // Quiz finished, show the score
            alert(`Your final score is: ${score + (answer === questions[questionIndex].answer ? 1 : 0)} out of ${questions.length}`);
        }
    };

    return (
        <div className="quiz-container">
            <h2 className="quiz-title">Quiz Time!</h2>
            <div className="quiz-question-container">
                {questionIndex < questions.length ? (
                    <>
                        <div className="quiz-header">
                            <span className="question-count">{`Question ${questionIndex + 1} of ${questions.length}`}</span>
                        </div>
                        <h4>{questions[questionIndex].question}</h4>
                        <div className="options-container">
                            {questions[questionIndex].options.map((option, idx) => (
                                <button
                                    key={idx}
                                    className="btn btn-option"
                                    onClick={() => handleAnswer(option)}
                                >
                                    {option}
                                </button>
                            ))}
                        </div>
                    </>
                ) : (
                    <div className="final-result">
                        <h3>Your Final Score: {score} out of {questions.length}</h3>
                        <button
                            className="btn btn-primary"
                            onClick={() => {
                                setQuestionIndex(0);
                                setScore(0);
                            }}
                        >
                            Try Again
                        </button>
                    </div>
                )}
            </div>

            {/* Inline CSS */}
            <style>{`
                .quiz-container {
                    background-color: #f9f9f9;
                    padding: 20px;
                    border-radius: 10px;
                    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
                    max-width: 600px;
                    margin: 50px auto;
                }

                .quiz-title {
                    text-align: center;
                    font-size: 32px;
                    margin-bottom: 20px;
                    color: #4CAF50;
                }

                .quiz-header {
                    display: flex;
                    justify-content: space-between;
                    margin-bottom: 15px;
                }

                .question-count {
                    font-size: 18px;
                    color: #888;
                }

                .quiz-question-container h4 {
                    font-size: 22px;
                    margin-bottom: 20px;
                    color: #333;
                }

                .options-container {
                    display: flex;
                    flex-direction: column;
                }

                .btn-option {
                    background-color: #007bff;
                    color: white;
                    padding: 10px;
                    margin-bottom: 10px;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                    font-size: 16px;
                }

                .btn-option:hover {
                    background-color: #0056b3;
                }

                .final-result {
                    text-align: center;
                    font-size: 24px;
                }

                .final-result button {
                    margin-top: 20px;
                }

                .final-result h3 {
                    color: #333;
                    font-size: 24px;
                    margin-bottom: 20px;
                }

                .btn-primary {
                    background-color: #28a745;
                    border: none;
                    padding: 10px 20px;
                    border-radius: 5px;
                    color: white;
                    font-size: 18px;
                }

                .btn-primary:hover {
                    background-color: #218838;
                }
            `}</style>
        </div>
    );
}

export default Quiz;
