import React, { useEffect, useState } from 'react';

const Profile = () => {
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Retrieve user data from localStorage
        const savedUser = localStorage.getItem('user');
        console.log('Saved user from localStorage:', savedUser);  // Debugging line

        if (savedUser) {
            setUser(JSON.parse(savedUser));  // Parse and set the user data
        }
    }, []);

    if (!user) {
        return <div>Loading...</div>;  // Show loading message while data is being fetched
    }

    return (
        <div className="d-flex justify-content-center align-items-start bg-light" style={{ paddingTop: '70px' }}>
            <div className="card shadow-lg p-4 w-100" style={{ maxWidth: '400px' }}>
                <h2 className="text-center mb-4 text-primary">User Profile</h2>
                <div className="mb-3">
                    <h4><strong>Name:</strong> {user.name}</h4>
                </div>
                <div className="mb-3">
                    <h4><strong>Email:</strong> {user.email}</h4>
                </div>
                <div className="mb-3">
                    <h4><strong>Phone:</strong> {user.phone}</h4>
                </div>
                <div className="mb-3">
                    <h4><strong>Pincode:</strong> {user.pincode}</h4>
                </div>
            </div>
        </div>
    );
};

export default Profile;
