import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function HomePage() {
    const [userProfile, setUserProfile] = useState(null);

    // Fetch user profile information when the page loads
    useEffect(() => {
        axios.get('http://localhost:3000/user-profile')  // Replace with your backend route
            .then((response) => {
                setUserProfile(response.data);
            })
            .catch((error) => {
                console.error('Error fetching user profile:', error);
            });
    }, []);

    return (
        <div className="container">
            <header className="my-5 text-center">
                <h1>Welcome to the Quiz App</h1>
                <p className="lead">Test your knowledge with our quiz!</p>
            </header>

            {/* About Section */}
            <section className="about-section py-5 bg-light">
                <div className="container">
                    <h2>About the Quiz</h2>
                    <p>
                        This quiz will test your knowledge in various topics.
                        You can attempt the quiz and check your results right after.
                        The quiz is available in both English and Hindi!
                    </p>
                </div>
            </section>

            {/* Attempt Quiz Section */}
            <section className="quiz-section text-center py-5">
                <div className="container">
                    <h2>Ready to Test Your Knowledge?</h2>
                    <Link to="/quiz" className="btn btn-primary btn-lg">
                        Attempt Quiz
                    </Link>
                </div>
            </section>

            {/* Profile Section */}
            {userProfile ? (
                <section className="profile-section bg-light py-5">
                    <div className="container">
                        <h2>Your Profile</h2>
                        <ul className="list-group">
                            <li className="list-group-item"><strong>Name:</strong> {userProfile.name}</li>
                            <li className="list-group-item"><strong>Email:</strong> {userProfile.email}</li>
                            <li className="list-group-item"><strong>Phone:</strong> {userProfile.phone}</li>
                            <li className="list-group-item"><strong>Pincode:</strong> {userProfile.pincode}</li>
                        </ul>
                    </div>
                </section>
            ) : (
                <section className="profile-section py-5">
                    <div className="container">
                        <h2>Your Profile</h2>
                        <p>You must register first to view your profile.</p>
                    </div>
                </section>
            )}
        </div>
    );
}

export default HomePage;
