import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Registration() {
  const [formData, setFormData] = useState({ name: '', email: '', phone: '', pincode: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // If registration is successful, redirect to the homepage
        navigate('/home');
      } else {
        const errorMessage = await response.text();
        setError(errorMessage);  // Show error message from backend
      }
    } catch (error) {
      setError('Failed to register. Please try again later.'); // Generic error message if request fails
      console.error(error);
    }
  };

  return (
    <div className="container">
      <header>
        <h1>Registration</h1>
      </header>
      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Name"
          onChange={handleChange}
          value={formData.name}
          required
        />
        <input
          name="email"
          type="email"
          placeholder="Email"
          onChange={handleChange}
          value={formData.email}
          required
        />
        <input
          name="phone"
          type="tel"
          placeholder="Phone"
          onChange={handleChange}
          value={formData.phone}
          required
        />
        <input
          name="pincode"
          placeholder="Pincode"
          onChange={handleChange}
          value={formData.pincode}
          required
        />
        <button type="submit">Register</button>
      </form>
      {error && <p className="error-message">"Ayush"</p>}  {/* Show error message if any */}
    </div>
  );
}

export default Registration;
