import React from 'react';
import Navbar from './Navbar'; // Import Navbar component
import { Link } from 'react-router-dom';

function HomePage() {
    return (
        <div>
            <Navbar />
            <div className="container mt-5 pt-5"> {/* Added pt-5 to give space after the navbar */}
                <header className="text-center mb-5">
                    <h1 className="home-heading">Welcome to the Quiz App</h1>
                    <p className="lead home-lead">Test your knowledge with our quiz!</p>
                </header>

                {/* About Section */}
                <section className="about-section py-5 bg-light">
                    <div className="container">
                        <h2 className="about-heading">About the Quiz</h2>
                        <p className="about-description">
                            This quiz will test your knowledge in various topics. You can attempt the quiz and check your results right after.
                            The quiz is available in both English and Hindi!
                        </p>
                    </div>
                </section>

                {/* Quiz Section */}
                <section className="quiz-section text-center py-5">
                    <div className="container">
                        <h2 className="quiz-heading">Ready to Test Your Knowledge?</h2>
                        <Link to="/quiz" className="btn btn-primary btn-lg quiz-btn">
                            Attempt Quiz
                        </Link>
                    </div>
                </section>
            </div>

            {/* Inline CSS to style the page */}
            <style jsx="true">{`
                /* General Styles */
                .home-heading {
                    font-size: 3rem;
                    font-weight: bold;
                    color: #2c3e50;
                    text-transform: uppercase;
                    letter-spacing: 2px;
                    margin-bottom: 20px;
                }

                .home-lead {
                    font-size: 1.2rem;
                    color: #34495e;
                    line-height: 1.5;
                    margin-bottom: 40px;
                }

                /* About Section */
                .about-heading {
                    font-size: 2rem;
                    font-weight: bold;
                    color: #3498db;
                    margin-bottom: 20px;
                }

                .about-description {
                    font-size: 1rem;
                    color: #2c3e50;
                    line-height: 1.7;
                    max-width: 800px;
                    margin: 0 auto;
                }

                /* Quiz Section */
                .quiz-heading {
                    font-size: 2rem;
                    font-weight: bold;
                    color: #2c3e50;
                    margin-bottom: 20px;
                }

                .quiz-btn {
                    padding: 15px 30px;
                    font-size: 1.2rem;  /* Increased button font size for better interaction */
                    text-transform: uppercase;
                    border-radius: 30px;
                    transition: all 0.3s ease;
                }

                /* Hover effect for button */
                .quiz-btn:hover {
                    background-color: #2980b9;
                    border-color: #2980b9;
                    transform: scale(1.05);
                }

                /* Extra Styling for Sections to look neat */
                .about-section {
                    padding-top: 50px;
                    padding-bottom: 50px;
                }

                .quiz-section {
                    padding-top: 50px;
                    padding-bottom: 50px;
                }

                /* Navbar Adjustment */
                .navbar {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    z-index: 1000;
                    background-color: #3498db;
                    padding: 0.5rem 1rem;
                }

                .container {
                    margin-top: 100px; /* Ensuring content doesn't get overlapped by the navbar */
                }
            `}</style>
        </div>
    );
}

export default HomePage;
