import React from 'react';

function HomePage() {
  const userName = localStorage.getItem('userName');

  return (
    <div className="container mt-5" style={{ textAlign: 'center' }}>
      <h1
        style={{
          fontSize: '3rem',
          fontWeight: 'bold',
          color: '#3498db',
          textTransform: 'uppercase',
          letterSpacing: '1px',
          marginBottom: '20px',
        }}
      >
        Welcome to the Quiz App!
      </h1>
      <p
        style={{
          fontSize: '1.2rem',
          color: '#2c3e50',
          lineHeight: '1.5',
          maxWidth: '600px',
          margin: '0 auto 40px',
        }}
      >
        Start by taking a quiz or learn more about us. Test your knowledge on various topics and see how well you score. Challenge yourself today!
      </p>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
        <a 
          href="/quiz" 
          className="btn btn-primary btn-lg"
        >
          Start Quiz
        </a>
        <a 
          href="/about" 
          className="btn btn-secondary btn-lg"
        >
          About Us
        </a>
      </div>
    </div>
  );
}

export default HomePage;
