// src/components/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  return (
    <div>
      {/* Inline CSS for the page */}
      <style>
        {`
          /* Navbar Styling */
          .navbar {
            background-color: #2c3e50; /* Dark blue background for navbar */
            padding: 15px 30px;
          }

          .navbar-brand {
            font-size: 2rem;
            color: #ecf0f1 !important;
            font-weight: bold;
            letter-spacing: 1px;
            text-transform: uppercase;
            text-align: center;
            flex-grow: 1;
          }

          .navbar-nav {
            display: flex;
            justify-content: center;
            gap: 30px; /* Horizontal spacing between items */
            margin-left: auto;
          }

          .navbar-nav .nav-item .nav-link {
            color: #ecf0f1 !important;
            font-size: 1.2rem;
            font-weight: 500;
            text-transform: uppercase;
            transition: all 0.3s ease;
          }

          .navbar-nav .nav-item .nav-link:hover {
            color: #f39c12 !important; /* Hover effect color */
            transform: translateY(-3px);
          }

          .navbar-nav .nav-item .nav-link.active {
            color: #f39c12 !important; /* Active link color */
          }

          .navbar-toggler-icon {
            background-color: #f39c12;
          }

          /* Main Content Styling */
          h1 {
            text-align: center;
            font-size: 3rem;
            color: #3498db;
            margin-top: 100px; /* Added margin to avoid overlap with navbar */
          }

          p {
            text-align: center;
            font-size: 1.2rem;
            color: #333;
            margin-top: 20px;
          }

          /* Sticky Navbar */
          .navbar {
            position: fixed;
            top: 0;
            width: 100%;
            z-index: 1000;
            box-shadow: 0 4px 2px -2px gray; /* Add some shadow for better visibility */
          }

          /* Padding to avoid content being hidden behind navbar */
          .main-content {
            padding-top: 100px; /* This ensures the content doesn't get hidden under the fixed navbar */
          }
        `}
      </style>

      {/* Navbar Section */}
      <nav className="navbar navbar-expand-lg navbar-dark">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">Quiz App</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <Link className="nav-link active" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about">About Us</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/quiz">Quiz</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/profile">Profile</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Main Content Section */}
      <div className="main-content">
        <h1>Welcome to the Quiz App!</h1>
        <p>Start by taking a quiz or learn more about us.</p>
      </div>
    </div>
  );
}

export default HomePage;
