import React, { useState } from 'react';

function Quiz() {
    const [score, setScore] = useState(0);
    const [questionIndex, setQuestionIndex] = useState(0);
    const [isAnswered, setIsAnswered] = useState(false);
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [feedback, setFeedback] = useState('');
    const [quizFinished, setQuizFinished] = useState(false);

    const questions = [
        { question: 'What is 2 + 2?', options: ['3', '4', '5'], answer: '4' },
        { question: 'What is 3 + 5?', options: ['8', '7', '6'], answer: '8' },
        { question: 'What is the capital of France?', options: ['Berlin', 'Paris', 'Madrid'], answer: 'Paris' },
        { question: 'Which planet is closest to the Sun?', options: ['Earth', 'Mars', 'Mercury'], answer: 'Mercury' },
        { question: 'What is the square root of 16?', options: ['2', '4', '8'], answer: '4' },
        { question: 'What is the largest ocean on Earth?', options: ['Atlantic', 'Pacific', 'Indian'], answer: 'Pacific' },
        { question: 'Which country is known as the Land of the Rising Sun?', options: ['China', 'Japan', 'Korea'], answer: 'Japan' },
        { question: 'Who wrote the play Romeo and Juliet?', options: ['Shakespeare', 'Dickens', 'Hemingway'], answer: 'Shakespeare' },
        { question: 'What is the chemical symbol for water?', options: ['H2O', 'O2', 'CO2'], answer: 'H2O' },
        { question: 'What is the currency of the United Kingdom?', options: ['Dollar', 'Pound', 'Euro'], answer: 'Pound' },
    ];

    const handleAnswer = (selected) => {
        setSelectedAnswer(selected);
        if (selected === questions[questionIndex].answer) {
            setScore(score + 1);
            setFeedback('Correct!');
        } else {
            setFeedback('Incorrect!');
        }
        setIsAnswered(true);
    };

    const nextQuestion = () => {
        if (questionIndex === questions.length - 1) {
            setQuizFinished(true);
        } else {
            setQuestionIndex(questionIndex + 1);
            setIsAnswered(false);
            setSelectedAnswer(null);
            setFeedback('');
        }
    };

    const tryAgain = () => {
        setScore(0);
        setQuestionIndex(0);
        setQuizFinished(false);
        setIsAnswered(false);
        setSelectedAnswer(null);
        setFeedback('');
    };

    return (
        <div className="quiz-container">
            <h2 className="quiz-title">Quiz Time!</h2>

            {!quizFinished ? (
                <div className="quiz-question-container">
                    <div className="question-header">
                        <span className="question-count">{`Question ${questionIndex + 1} of ${questions.length}`}</span>
                        <span className="score-display">Score: {score}</span>
                    </div>

                    <h4>{questions[questionIndex].question}</h4>

                    <div className="options-container">
                        {questions[questionIndex].options.map((option, idx) => (
                            <button
                                key={idx}
                                className={`btn btn-option 
                                    ${isAnswered && option === questions[questionIndex].answer ? 'correct' : ''} 
                                    ${isAnswered && option !== questions[questionIndex].answer && option === selectedAnswer ? 'incorrect' : ''}`}
                                onClick={() => handleAnswer(option)}
                                disabled={isAnswered}
                            >
                                {option}
                            </button>
                        ))}
                    </div>

                    {isAnswered && (
                        <div className="feedback">
                            <p>{feedback}</p>
                            <div className="next-button-container">
                                <button className="btn btn-next" onClick={nextQuestion}>
                                    Next Question
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            ) : (
                <div className="final-result">
                    <h3>Quiz Finished!</h3>
                    <h4>Your Final Score: {score} out of {questions.length}</h4>
                    <button className="btn btn-primary" onClick={tryAgain}>
                        Try Again
                    </button>
                </div>
            )}

            {/* Inline CSS */}
            <style>{`
                .quiz-container {
                    max-width: 600px;
                    margin: 0 auto;
                    padding: 20px;
                    background-color: #fff;
                    border-radius: 8px;
                    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
                    margin-top: 60px;
                }

                .quiz-title {
                    text-align: center;
                    color: #4CAF50;
                    font-size: 28px;
                    margin-bottom: 20px;
                }

                .quiz-question-container {
                    padding: 20px;
                    background-color: #f9f9f9;
                    border-radius: 10px;
                    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.05);
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                }

                .question-header {
                    display: flex;
                    justify-content: space-between;
                    margin-bottom: 20px;
                    width: 100%;
                    font-size: 16px;
                    color: #555;
                    font-weight: 600;
                }

                .question-count {
                    font-size: 16px;
                    color: #4CAF50;
                }

                .score-display {
                    font-size: 16px;
                    font-weight: bold;
                    color: #4CAF50;
                }

                h4 {
                    color: #333;
                    font-size: 20px;
                    margin-bottom: 20px;
                    text-align: center;
                }

                .options-container {
                    display: flex;
                    flex-direction: column;
                    gap: 15px;
                    text-align: center;
                    width: 100%;
                }

                .btn-option {
                    background-color: #007bff;
                    color: white;
                    padding: 15px 25px;
                    font-size: 18px;
                    border: none;
                    border-radius: 8px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
                }

                .btn-option:hover {
                    background-color: #0056b3;
                    box-shadow: 0 6px 8px rgba(0, 0, 0, 0.2);
                    transform: translateY(-2px);
                }

                .btn-option.correct {
                    background-color: #28a745;
                }

                .btn-option.incorrect {
                    background-color: #dc3545;
                }

                .btn-option:disabled {
                    background-color: #ddd;
                    cursor: not-allowed;
                    box-shadow: none;
                }

                .btn-option:focus {
                    outline: none;
                    border: 2px solid #007bff;
                    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
                }

                .feedback {
                    text-align: center;
                    margin-top: 20px;
                    font-size: 18px;
                    color: #333;
                }

                .next-button-container {
                    margin-top: 20px;
                    text-align: center;
                }

                .btn-next {
                    background-color: #28a745;
                    border: none;
                    padding: 12px 20px;
                    font-size: 18px;
                    color: white;
                    border-radius: 5px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                }

                .btn-next:hover {
                    background-color: #218838;
                }

                .final-result {
                    text-align: center;
                    padding: 30px;
                    background-color: #f0f0f0;
                    border-radius: 10px;
                    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
                }

                .final-result h3 {
                    font-size: 24px;
                    margin-bottom: 10px;
                    color: #333;
                }

                .final-result h4 {
                    font-size: 22px;
                    margin-bottom: 15px;
                    color: #4CAF50;
                }

                .final-result button {
                    background-color: #007bff;
                    color: white;
                    padding: 12px 20px;
                    font-size: 16px;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                }

                .final-result button:hover {
                    background-color: #0056b3;
                }
            `}</style>
        </div>
    );
}

export default Quiz;
