 import { Component } from 'react';
class App extends Component{
    constructor(){
        this.state = {
            x:[]
        }
    }

    async componentDidMount(){
        const data = await fetch('https://api.github.com/users/octocat/repos');
         const jasonData = await data.json();
         this.setState({x:jasonData});
    }

    render(){
        const x = this.state;
        return(
            <>
            <p>
                {
                   x && x.map(item=>(<p>{item.name}</p>))
                }
            </p>
            </>
        );
    }
}
export default App;
