import React, { useState } from 'react';
import axios from 'axios';

function Signup() {
    const [values, setValues] = useState({
        name: '',
        email: '',
        phone: '',
        pincode: ''
    });

    const handleChange = (event) => {
        setValues({ ...values, [event.target.name]: event.target.value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        
        // Send data to backend for registration
        axios.post('http://localhost:3000/signup', values)
            .then(res => {
                console.log("Registered Successfully", res.data);
                // Optionally, you can redirect or reset the form here
            })
            .catch(err => {
                console.log("Error:", err);
            });
    };

    return (
        <div className='d-flex w-100 vh-100 bg-primary justify-content-center align-items-center'>
            {/* Form container with a box-like appearance */}
            <div className='bg-light p-5 rounded shadow-lg w-100' style={{ maxWidth: '400px' }}>
                <h2 className='text-center mb-4 text-primary'>Sign-Up</h2>
                
                <form onSubmit={handleSubmit}>
                    {/* Name Field */}
                    <div className='mb-3'>
                        <label htmlFor="name" className='form-label'><strong>Name</strong></label>
                        <input type="text" id="name" name="name" placeholder='Enter Name'
                            className='form-control' required onChange={handleChange} />
                    </div>

                    {/* Email Field */}
                    <div className='mb-3'>
                        <label htmlFor="email" className='form-label'><strong>Email</strong></label>
                        <input type="email" id="email" name="email" placeholder='Enter Email'
                            className='form-control' required onChange={handleChange} />
                    </div>

                    {/* Phone Field */}
                    <div className='mb-3'>
                        <label htmlFor="phone" className='form-label'><strong>Phone</strong></label>
                        <input type="text" id="phone" name="phone" placeholder='Enter Phone Number'
                            className='form-control' required maxLength="15" onChange={handleChange} />
                    </div>

                    {/* Pincode Field */}
                    <div className='mb-3'>
                        <label htmlFor="pincode" className='form-label'><strong>Pincode</strong></label>
                        <input type="text" id="pincode" name="pincode" placeholder='Enter Pincode'
                            className='form-control' required maxLength="6" onChange={handleChange} />
                    </div>

                    {/* Submit Button */}
                    <button type='submit' className='btn btn-success w-100 py-2 rounded-0'>Sign up</button>
                    
                    {/* Terms and Conditions Link */}
                    <p className='text-center mt-3'>
                        You agree to{' '}
                        <a href="/" className='text-decoration-none'>Terms and Conditions</a>
                    </p>
                </form>
            </div>
        </div>
    );
}

export default Signup;
