import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Registration() {
  const [formData, setFormData] = useState({ name: '', email: '', phone: '', pincode: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });
    if (response.ok) navigate('/home');
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registration</h2>
      <input name="name" placeholder="Name" onChange={handleChange} required />
      <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
      <input name="phone" type="tel" placeholder="Phone" onChange={handleChange} required />
      <input name="pincode" placeholder="Pincode" onChange={handleChange} required />
      <button type="submit">Register</button>
    </form>
  );
}

export default Registration;
