import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Navbar() {
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Check if there's a userName in localStorage and set it to state
    const storedUserName = localStorage.getItem('userName');
    if (storedUserName) {
      setUserName(storedUserName);
    }
  }, []);

  const handleLogout = () => {
    // Clear the userName from localStorage and reset state
    localStorage.removeItem('userName');
    setUserName('');
    navigate('/login'); // Navigate to login page after logout
  };

  return (
    <nav
      className="navbar navbar-expand-lg navbar-dark"
      style={{
        backgroundColor: '#3498db',
        position: 'fixed',
        width: '100%',
        zIndex: '1000',
        padding: '10px 0',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      <div className="container-fluid">
        <a className="navbar-brand" href="#" style={{ fontSize: '1.8rem', fontWeight: 'bold' }}>
          Quiz App
        </a>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto" style={{ display: 'flex', justifyContent: 'center', marginTop: '0' }}>
            <li className="nav-item">
              <Link className="nav-link" to="/home" style={linkStyles}>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/about" style={linkStyles}>
                About Us
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/quiz" style={linkStyles}>
                Quiz
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/profile" style={linkStyles}>
                Profile
              </Link>
            </li>

            {/* Display user name if available */}
            {userName ? (
              <>
                <li className="nav-item" style={{ marginLeft: '20px' }}>
                  <span className="nav-link" style={{ color: 'white' }}>
                    Welcome, {userName}
                  </span>
                </li>
                <li className="nav-item">
                  <button
                    className="nav-link"
                    style={{
                      color: 'white',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer',
                      fontSize: '1.1rem',
                    }}
                    onClick={handleLogout}
                  >
                    Logout
                  </button>
                </li>
              </>
            ) : (
              // If not logged in, show "Login" link
              <li className="nav-item">
                <Link className="nav-link" to="/login" style={linkStyles}>
                  Login
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}

// Link style with hover effect for modern touch
const linkStyles = {
  fontSize: '1.1rem',
  fontWeight: '600',
  textTransform: 'uppercase',
  letterSpacing: '1px',
  padding: '8px 12px',
  transition: '0.3s ease',
  textDecoration: 'none',
  color: 'white',
};

export default Navbar;
