import React, { useState } from 'react';

function Quiz() {
    const [score, setScore] = useState(0);
    const [questionIndex, setQuestionIndex] = useState(0);
    const [isAnswered, setIsAnswered] = useState(false);
    const [quizFinished, setQuizFinished] = useState(false);
    const [answerStatus, setAnswerStatus] = useState('');

    const questions = [
        { 
            question: 'What is 2 + 2?', 
            options: ['3', '4', '5'], 
            answer: '4', 
            description: 'Let\'s start with a simple addition question.' 
        },
        { 
            question: 'What is 3 + 5?', 
            options: ['8', '7', '6'], 
            answer: '8', 
            description: 'Another simple addition problem to test your basic math skills.' 
        },
        { 
            question: 'What is the capital of France?', 
            options: ['Berlin', 'Paris', 'Madrid'], 
            answer: 'Paris', 
            description: 'Let\'s check your knowledge of world capitals.' 
        },
        { 
            question: 'Which planet is closest to the Sun?', 
            options: ['Earth', 'Mars', 'Mercury'], 
            answer: 'Mercury', 
            description: 'A question for all the space enthusiasts!' 
        },
        { 
            question: 'What is the square root of 16?', 
            options: ['2', '4', '8'], 
            answer: '4', 
            description: 'Test your knowledge of basic math operations.' 
        },
        { 
            question: 'What is the largest ocean on Earth?', 
            options: ['Atlantic', 'Pacific', 'Indian'], 
            answer: 'Pacific', 
            description: 'A geography question about Earth\'s oceans.' 
        },
        { 
            question: 'Which country is known as the Land of the Rising Sun?', 
            options: ['China', 'Japan', 'Korea'], 
            answer: 'Japan', 
            description: 'A cultural and geographical question about Japan.' 
        },
        { 
            question: 'Who wrote the play Romeo and Juliet?', 
            options: ['Shakespeare', 'Dickens', 'Hemingway'], 
            answer: 'Shakespeare', 
            description: 'A literature question to test your knowledge of famous playwrights.' 
        },
        { 
            question: 'What is the chemical symbol for water?', 
            options: ['H2O', 'O2', 'CO2'], 
            answer: 'H2O', 
            description: 'A simple science question about water.' 
        },
        { 
            question: 'What is the currency of the United Kingdom?', 
            options: ['Dollar', 'Pound', 'Euro'], 
            answer: 'Pound', 
            description: 'A question about international currencies.' 
        },
    ];

    const handleAnswer = (selectedAnswer) => {
        if (!isAnswered) {
            if (selectedAnswer === questions[questionIndex].answer) {
                setScore(score + 1);
                setAnswerStatus('Correct!');
            } else {
                setAnswerStatus('Incorrect!');
            }
            setIsAnswered(true);
        }
    };

    const nextQuestion = () => {
        if (questionIndex === questions.length - 1) {
            setQuizFinished(true);
        } else {
            setQuestionIndex(questionIndex + 1);
            setIsAnswered(false);
            setAnswerStatus('');
        }
    };

    const getQuote = () => {
        if (score >= 8) return 'Amazing job! You are a quiz master!';
        if (score >= 5) return 'Good job! Keep it up!';
        return 'Don’t give up! Keep learning and practicing!';
    };

    return (
        <div className="quiz-container">
            <h2 className="quiz-title">Quiz Time!</h2>

            <div className="quiz-question-container">
                {!quizFinished ? (
                    <div className="question-section">
                        <div className="question-header">
                            {/* Score always displayed */}
                            <span className="score-display">Score: {score} / {questions.length}</span>
                        </div>

                        {/* Display the description/heading above the question */}
                        <p className="question-description">{questions[questionIndex].description}</p>

                        <h4>{questions[questionIndex].question}</h4>

                        <div className="options-container">
                            {questions[questionIndex].options.map((option, idx) => (
                                <button
                                    key={idx}
                                    className={`btn btn-option ${isAnswered && option === questions[questionIndex].answer ? 'correct' : ''} ${isAnswered && option !== questions[questionIndex].answer && option === selectedAnswer ? 'incorrect' : ''}`}
                                    onClick={() => handleAnswer(option)}
                                    disabled={isAnswered}
                                >
                                    {option}
                                </button>
                            ))}
                        </div>

                        {/* Show whether the answer is correct or incorrect */}
                        {isAnswered && <p className="answer-status">{answerStatus}</p>}

                        {isAnswered && (
                            <div className="next-button-container">
                                <button className="btn btn-next" onClick={nextQuestion}>
                                    Next Question
                                </button>
                            </div>
                        )}
                    </div>
                ) : (
                    <div className="final-result">
                        <h3>Quiz Finished!</h3>
                        <h4>Your Final Score: {score} out of {questions.length}</h4>
                        <h5>{getQuote()}</h5>
                        <button className="btn btn-primary" onClick={() => window.location.reload()}>
                            Try Again
                        </button>
                    </div>
                )}
            </div>

            {/* Inline CSS */}
            <style>{`
                .quiz-container {
                    max-width: 600px;
                    margin: 0 auto;
                    padding: 20px;
                    background-color: #fff;
                    border-radius: 8px;
                    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
                }

                .quiz-title {
                    text-align: center;
                    color: #4CAF50;
                    font-size: 28px;
                    margin-bottom: 20px;
                }

                .quiz-question-container {
                    padding: 20px;
                    background-color: #f9f9f9;
                    border-radius: 10px;
                    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.05);
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                }

                .question-header {
                    display: flex;
                    justify-content: flex-start;
                    width: 100%;
                    font-size: 16px;
                    color: #555;
                    font-weight: 600;
                    margin-bottom: 20px;
                }

                .score-display {
                    font-size: 18px;
                    color: #4CAF50;
                    font-weight: bold;
                }

                h4 {
                    color: #333;
                    font-size: 20px;
                    margin-bottom: 20px;
                    text-align: center;
                }

                .question-description {
                    color: #555;
                    font-size: 16px;
                    margin-bottom: 15px;
                    text-align: center;
                    font-style: italic;
                }

                .options-container {
                    display: flex;
                    flex-direction: column;
                    gap: 10px;
                    text-align: center;
                }

                .btn-option {
                    background-color: #007bff;
                    color: white;
                    padding: 12px;
                    font-size: 16px;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                }

                .btn-option:hover {
                    background-color: #0056b3;
                }

                .btn-option.correct {
                    background-color: #28a745;
                }

                .btn-option.incorrect {
                    background-color: #dc3545;
                }

                .btn-option:disabled {
                    background-color: #ddd;
                    cursor: not-allowed;
                }

                .answer-status {
                    color: #28a745;
                    font-size: 18px;
                    margin-top: 10px;
                    font-weight: bold;
                }

                .next-button-container {
                    margin-top: 20px;
                    text-align: center;
                }

                .btn-next {
                    background-color: #28a745;
                    border: none;
                    padding: 10px 20px;
                    font-size: 18px;
                    color: white;
                    border-radius: 5px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                }

                .btn-next:hover {
                    background-color: #218838;
                }

                .final-result {
                    text-align: center;
                    padding: 30px;
                    background-color: #f0f0f0;
                    border-radius: 10px;
                    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
                }

                .final-result h3 {
                    font-size: 24px;
                    margin-bottom: 10px;
                    color: #333;
                }

                .final-result h4 {
                    font-size: 22px;
                    margin-bottom: 15px;
                    color: #4CAF50;
                }

                .final-result h5 {
                    font-size: 20px;
                    margin-bottom: 25px;
                    color: #4CAF50;
                }

                .final-result button {
                    background-color: #007bff;
                    color: white;
                    padding: 12px 20px;
                    font-size: 16px;
                    border: none;
                    border-radius: 5px;
                    cursor: pointer;
                }

                .final-result button:hover {
                    background-color: #0056b3;
                }
            `}</style>
        </div>
    );
}

export default Quiz;
