import React from 'react';

function HomePage() {
  // Get the user name from localStorage
  const userName = localStorage.getItem('userName');

  return (
    <div className="container mt-5" style={{ paddingTop: '80px', textAlign: 'center' }}>
      <h1 
        style={{
          fontSize: '3rem',
          fontWeight: 'bold',
          color: '#3498db',
          textTransform: 'uppercase',
          letterSpacing: '1px',
          marginBottom: '20px',
        }}
      >
        Welcome to the Quiz App!
      </h1>
      
      <p 
        style={{
          fontSize: '1.2rem',
          color: '#2c3e50',
          lineHeight: '1.5',
          maxWidth: '600px',
          margin: '0 auto 40px', // Centering the paragraph and adding margin bottom
        }}
      >
        Start by taking a quiz or learn more about us. Test your knowledge on various topics and see how well you score. Challenge yourself today!
      </p>

      {/* Profile Section in the Navbar (Right Corner) */}
      <div className="profile-section" style={{ position: 'absolute', top: '20px', right: '20px' }}>
        {userName ? (
          <span style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>
            Welcome, {userName}!
          </span>
        ) : (
          <span>Welcome, Guest!</span>
        )}
      </div>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
        <a 
          href="/quiz" 
          className="btn btn-primary btn-lg" 
          style={{
            padding: '15px 30px',
            fontSize: '1.1rem',
            textTransform: 'uppercase',
            borderRadius: '30px',
            transition: 'all 0.3s ease',
          }}
        >
          Start Quiz
        </a>
        
        <a 
          href="/about" 
          className="btn btn-secondary btn-lg" 
          style={{
            padding: '15px 30px',
            fontSize: '1.1rem',
            textTransform: 'uppercase',
            borderRadius: '30px',
            transition: 'all 0.3s ease',
          }}
        >
          About Us
        </a>
      </div>
    </div>
  );
}

export default HomePage;
